namespace Mageplaza\SeoCrosslinks\Setup;

use Magento\Framework\Setup\ModuleContextInterface;
use Magento\Framework\Setup\SchemaSetupInterface;
use Magento\Framework\Setup\UpgradeSchemaInterface;

/**
 * Class UpgradeSchema
 * @package Mageplaza\SeoCrosslinks\Setup
 */
class UpgradeSchema implements UpgradeSchemaInterface
{
    /**
     * upgrade
     *
     * @param SchemaSetupInterface $setup
     * @param ModuleContextInterface $context
     */
    public function upgrade(SchemaSetupInterface $setup, ModuleContextInterface $context)
    {
        $installer = $setup;
        $installer->startSetup();

        if (version_compare($context->getVersion(), '1.0.1') < 0) {
            $setup->getConnection()->dropColumn($setup->getTable('mageplaza_seocrosslinks_term'), 'from_date');
            $setup->getConnection()->dropColumn($setup->getTable('mageplaza_seocrosslinks_term'), 'to_date');
        }

        $installer->endSetup();
    }
}
